// === Data Table Service ===
// Data Mapping ---
export function mapTableSource(keys, object) {
    return object.map((origRow) => {
        const mappedRow = {};
        for (let i = 0; i < keys.length; i++) {
            mappedRow[keys[i]] = origRow[keys[i]];
        }
        return mappedRow;
    });
}
export function sort(arr, key, isDescending = false) {
    if (isDescending)
        return sortDesc(arr, key);
    return sortAsc(arr, key);
}
export function sortAsc(arr, key) {
    return arr
        .sort((x, y) => typeof x[key] === "string" && typeof y[key] === "string"
        ? (String(x[key])).localeCompare(String(y[key]))
        : x[key] - y[key]);
}
export function sortDesc(arr, key) {
    return arr
        .sort((x, y) => typeof y[key] === "string" && typeof x[key] === "string"
        ? (String(y[key])).localeCompare(String(x[key]))
        : y[key] - x[key]);
}
